setup.source:
class DefaultLabelNotLastInSwitchStmt {

    void foo(final int a) {
        switch (a) {
            case 1: // do something
                break;
            default: // the default case should be last, by convention
                System.out.print(a);
                break;
            case 2:
                break;
        }
    }

}

expected.source:
class DefaultLabelNotLastInSwitchStmt {

    void foo(final int a) {
        switch (a) {
            case 1: // do something
                break;
            case 2:
                break;
            default: // the default case should be last, by convention
                System.out.print(a);
                break;
        }
    }

}

