setup.source:
public class MethodReturnsInternalArray {
    private int[] bar = new int[] { 1, 2, 3 };
    public int[] foo() {
        return bar;
    }
}

expected.source:
public class MethodReturnsInternalArray {
    private int[] bar = new int[] { 1, 2, 3 };
    public int[] foo() {
        return bar.clone();
    }
}

setup.source:
public class MethodReturnsInternalArray {
    private int[] bar = new int[] { 1, 2, 3 };
    public int[] foo() {
        return this.bar;
    }
}

expected.source:
public class MethodReturnsInternalArray {
    private int[] bar = new int[] { 1, 2, 3 };
    public int[] foo() {
        return this.bar.clone();
    }
}

setup.source:
public class MethodReturnsInternalArray {
    public int[] foo() {
        return bar();
    }
    private int[] bar() {
        new int[] { 1, 2, 3 };
    }
}

expected.source:
public class MethodReturnsInternalArray {
    public int[] foo() {
        return bar().clone();
    }
    private int[] bar() {
        new int[] { 1, 2, 3 };
    }
}
