setup.source:
public class AddEmptyString {
    public String foo() {
        return "" + 123;
    }
}

expected.source:
public class AddEmptyString {
    public String foo() {
        return String.valueOf(123);
    }
}

setup.source:
public class AddEmptyString {
    public String foo(int n) {
        return "" + n;
    }
}

expected.source:
public class AddEmptyString {
    public String foo(int n) {
        return String.valueOf(n);
    }
}

