setup.source:
public class AppendCharacterWithChar {
    public boolean foo(StringBuffer s) {
        return s.append("a");
    }
}

expected.source:
public class AppendCharacterWithChar {
    public boolean foo(StringBuffer s) {
        return s.append('a');
    }
}

