setup.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.startsWith("a");
    }
}

expected.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.charAt(0) == 'a';
    }
}

setup.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.startsWith("\n");
    }
}

expected.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.charAt(0) == '\n';
    }
}

setup.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.startsWith("\013");
    }
}

expected.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.charAt(0) == '\013';
    }
}

setup.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.startsWith("\u2026");
    }
}

expected.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return s.charAt(0) == '\u2026';
    }
}

setup.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return (s + s).startsWith("a");
    }
}

expected.source:
public class SimplifyStartsWith {
    public boolean foo(String s) {
        return (s + s).charAt(0) == 'a';
    }
}
