setup.source:
public class UnnecessaryCaseChange {
    public String foo(String s) {
        return s.toUpperCase().equals("baz");
    }
}

expected.source:
public class UnnecessaryCaseChange {
    public String foo(String s) {
        return s.equalsIgnoreCase("baz");
    }
}

